import "./App.css";

import DataTable from "react-data-table-component";
import { useEffect, useState } from "react";
import axios from "axios";

const columns = [
    {
        name: "id",
        selector: (row) => row.id,
    },
    {
        name: "name",
        selector: (row) => row.name,
    },
    {
        name: "detail",
        selector: (row) => row.detail,
    },
    {
        name: "coverimage",
        selector: (row) => row.coverimage,
    },
    {
        name: "latitude",
        selector: (row) => row.latitude,
    },
    {
        name: "longitude",
        selector: (row) => row.longitude,
    },
];

function App() {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [totalRows, setTotalRows] = useState(0);
    const [perPage, setPerPage] = useState(10);
    const [page, setPage] = useState(1);
    const [sortColumn, setSortColumn] = useState("");
    const [sortColumnDir, setSortColumnDir] = useState("");

    const fetchUsers = async () => {
        setLoading(true);
        // http://localhost:5000/api/attractions?page=1&per_page=10&sort_column=id&sort_direction=asc&search=Phi Phi
        const response = await axios.get(
            `http://localhost:5000/api/attractions?page=${page}&per_page=${perPage}`
        );

        setData(response.data.data);
        setTotalRows(response.data.total);
        setLoading(false);
    };

    const handlePageChange = (page) => {
        setPage(page);
    };

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
    };

    const handleSort = (column, sortDirection) => {
        // simulate server sort
        console.log(column, sortDirection);
    };

    useEffect(() => {
        fetchUsers(); // fetch page 1 of users
    }, [page, perPage]);

    return (
        <DataTable
            title="Users"
            columns={columns}
            data={data}
            progressPending={loading}
            pagination
            onSort={handleSort}
            paginationServer
            paginationTotalRows={totalRows}
            onChangeRowsPerPage={handlePerRowsChange}
            onChangePage={handlePageChange}
        />
    );
}

export default App;
